import React, { useState, useEffect, useCallback } from "react";
import { StyleSheet, Text, View, ScrollView, Image } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { beachSelectors } from "../../store/selectors";
import { beachActions } from "../../store/actions";
import { Card, CardItem, Body, Thumbnail, Left, Right } from "native-base";

const dummyData = [
  {
    _id: 1,
    name: "Buye",
    location: "Cabo Rojo"
  },
  {
    _id: 2,
    name: "Buye",
    location: "Cabo Rojo"
  },
  {
    _id: 2,
    name: "Buye",
    location: "Cabo Rojo"
  }
];

const Home = () => {
  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const dispatch = useDispatch();
  const beaches = useSelector(beachSelectors.selectBeaches);
  const beachesLoading = useSelector(beachSelectors.selectBeachesLoading);

  //This needs to be called through a ddispatch

  useEffect(() => {
    onEntry();
  }, [dispatch]);

  const onEntry = useCallback(async () => {
    await dispatch(beachActions.getBeaches());
  }, [dispatch]);

  // Eventually move this to its' own component

  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
      <Text style={styles.Header}>Beach Quality Report</Text>
      <ScrollView showsVerticalScrollIndicator={false}>
        {beachesLoading ? (
          <Text>Loading...</Text>
        ) : beaches ? (
          <React.Fragment>
            {dummyData.map(beach => {
              return (
                <Card style={styles.beachCard}>
                  <CardItem header>
                    <Text>{beach.name}</Text>
                  </CardItem>

                  <CardItem>
                    <Image
                      source={{
                        uri:
                          "http://cdn.c.photoshelter.com/img-get/I0000x8LjeqlKP0o/s/860/860/Playa-Buye-Cabo-Rojo-P-R-DSC0091.jpg"
                      }}
                      style={styles.beachImage}
                    />
                  </CardItem>
                  <Left>
                    <CardItem>
                      <Text>Location: {beach.location}</Text>
                    </CardItem>
                  </Left>
                  <CardItem>
                    <Text></Text>
                  </CardItem>
                </Card>
              );
            })}
          </React.Fragment>
        ) : null}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center"
  },
  Header: {
    backgroundColor: "#3498db",
    color: "#fff",
    paddingTop: 50,
    paddingBottom: 25,
    textAlign: "center",
    width: "100%",
    height: 100,
    alignItems: "center",
    justifyContent: "center",
    fontSize: 20,
    marginBottom: 50
  },
  beachCard: {
    minWidth: 250
  },
  beachImage: {
    height: 200,
    width: null,
    flex: 1
  }
});

export default Home;
